import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const FormDisplay = () => {
  const { formName } = useParams(); // Get the form name from the route parameter
  const [formData, setFormData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchFormData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/forms/${formName}`);
        if (!response.ok) {
          throw new Error(`Failed to fetch form: ${response.status}`);
        }
        const data = await response.json();
        setFormData(data);
      } catch (err) {
        setError('Form not found or error fetching the form data');
        console.error(err);
      }
    };

    fetchFormData();
  }, [formName]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!formData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{formData.name}</h2>
      <p>{formData.label}</p>
      {/* Render the form content as needed */}
    </div>
  );
};

export default FormDisplay;
