// src/components/FormDisplay.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './FormDisplay.css';

const FormDisplay = () => {
    const { formName } = useParams(); // Get form name from the URL
    const [formData, setFormData] = useState(null);
    const [formValues, setFormValues] = useState({}); // Store user input
    const [error, setError] = useState('');

    // Fetch form data from the backend when the component mounts
    useEffect(() => {
        const fetchFormData = async () => {
            try {
                const response = await fetch(`http://localhost:5000/forms/${encodeURIComponent(formName)}`); // Ensure the form name is encoded
                if (!response.ok) {
                    throw new Error('Form not found');
                }
                const data = await response.json();
                setFormData(parseXML(data.xml_data)); // Parse the XML data from the response
            } catch (error) {
                console.error('Error fetching form data:', error);
                setError('Form not found or an error occurred while fetching the data');
            }
        };
    
        fetchFormData();
    }, [formName]);
    

    // Helper function to parse XML data
    const parseXML = (xmlString) => {
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlString, 'application/xml');
        const name = xmlDoc.querySelector('form > name')?.textContent || 'Unnamed Form';
        const label = xmlDoc.querySelector('form > label')?.textContent || 'event_data';

        const elements = Array.from(xmlDoc.querySelectorAll('elements > element')).map((el) => {
            const optionsNode = el.querySelector('dropdownOptions');
            const options = optionsNode ? Array.from(optionsNode.querySelectorAll('option')).map(opt => opt.textContent) : [];

            return {
                type: el.querySelector('type')?.textContent || 'text',
                key: el.querySelector('key')?.textContent || '',
                label: el.querySelector('label')?.textContent || '',
                required: el.querySelector('required')?.textContent === 'true',
                placeholder: el.querySelector('placeholder')?.textContent || '',
                alignment: el.querySelector('alignment')?.textContent || 'center',
                options: options
            };
        });

        return { name, label, elements };
    };

    const handleInputChange = (key, value) => {
        setFormValues((prev) => ({ ...prev, [key]: value }));
    };

    const handleFileChange = (key, file) => {
        if (file) {
            const reader = new FileReader();
            reader.onload = (e) => {
                setFormValues((prev) => ({
                    ...prev,
                    [`file_${key}`]: e.target.result, // Store base64 encoded file
                    [`file_name_${key}`]: file.name,
                    [`file_path_${key}`]: file.webkitRelativePath || file.name,
                    [`file_size_${key}`]: file.size,
                    [`file_type_${key}`]: file.type,
                    [`file_last_modified_${key}`]: new Date(file.lastModified).toISOString(),
                }));
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        alert('Form submitted. Implement data handling logic here.');
    };

    if (!formData) {
        return <h2>{error || 'Loading form data...'}</h2>;
    }

    const renderElement = (element, index) => {
        switch (element.type) {
            case 'boolean':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                        <select
                            required={element.required}
                            onChange={(e) => handleInputChange(element.key, e.target.value)}
                            style={{ padding: '10px', borderRadius: '6px', width: '100%', maxWidth: '400px' }}
                        >
                            <option value="">Select an option</option>
                            <option value="true">True</option>
                            <option value="false">False</option>
                        </select>
                    </div>
                );
            case 'dateTime':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                        <input
                            type="date"
                            placeholder={element.placeholder}
                            required={element.required}
                            onChange={(e) => handleInputChange(element.key, e.target.value)}
                            style={{ padding: '10px', borderRadius: '6px', width: '100%', maxWidth: '400px' }}
                        />
                    </div>
                );
            case 'number':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                        <input
                            type="number"
                            placeholder={element.placeholder}
                            required={element.required}
                            onChange={(e) => handleInputChange(element.key, e.target.value)}
                            style={{ padding: '10px', borderRadius: '6px', width: '100%', maxWidth: '400px' }}
                        />
                    </div>
                );
            case 'dropdown':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                        <select
                            required={element.required}
                            onChange={(e) => handleInputChange(element.key, e.target.value)}
                            style={{ padding: '10px', borderRadius: '6px', width: '100%', maxWidth: '400px' }}
                        >
                            <option value="">Select an option</option>
                            {element.options.map((option, idx) => (
                                <option key={idx} value={option}>{option}</option>
                            ))}
                        </select>
                    </div>
                );
            case 'divider':
                return (
                    <hr key={index} className="form-divider" style={{ width: '100%', borderTop: '1px solid #444', margin: '20px 0' }} />
                );
            case 'button':
                if (element.key === 'Submit') {
                    return null; // Do not render this button here, as we will use the form's submit button instead
                }
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        <button
                            type="button"
                            onClick={() => alert(`${element.label} button clicked!`)}
                            style={{
                                padding: '10px 20px',
                                borderRadius: '6px',
                                backgroundColor: '#007bff',
                                color: 'white',
                                border: 'none',
                                cursor: 'pointer',
                            }}
                        >
                            {element.label || 'Button'}
                        </button>
                    </div>
                );
            case 'text':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                    </div>
                );
            case 'heading':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <h1
                                style={{
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </h1>
                        )}
                    </div>
                );
            case 'file':
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                        <input
                            type="file"
                            required={element.required}
                            onChange={(e) => handleFileChange(element.key, e.target.files[0])}
                            style={{ padding: '10px', borderRadius: '6px', width: '100%', maxWidth: '400px' }}
                        />
                    </div>
                );
            default:
                return (
                    <div key={index} className="form-group" style={{ textAlign: element.alignment, marginBottom: '15px' }}>
                        {element.label && (
                            <label
                                style={{
                                    display: 'block',
                                    marginBottom: '5px',
                                    fontWeight: 'bold',
                                    color: '#ffffff',
                                    textAlign: element.alignment,
                                }}
                            >
                                {element.label}
                            </label>
                        )}
                        <input
                            type={element.type}
                            placeholder={element.placeholder}
                            required={element.required}
                            onChange={(e) => handleInputChange(element.key, e.target.value)}
                            style={{ padding: '10px', borderRadius: '6px', width: '100%', maxWidth: '400px' }}
                        />
                    </div>
                );
        }
    };

    return (
        <div className="form-container">
            <h2>{formData.name}</h2>
            <form onSubmit={handleSubmit} style={{ maxHeight: '80vh', overflowY: 'auto' }}>
                {formData.elements.map((element, index) => renderElement(element, index))}
                <div className="form-group" style={{ textAlign: 'center', marginTop: '20px' }}>
                    <button
                        type="submit"
                        id="submit-button"
                        style={{
                            padding: '10px 20px',
                            borderRadius: '6px',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            cursor: 'pointer',
                        }}
                    >
                        Submit
                    </button>
                </div>
            </form>
        </div>
    );
};

export default FormDisplay;
