import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CreateUser.css';

const CreateUser = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('Select Role');
  const [users, setUsers] = useState([]); // To fetch existing users
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    // Fetch existing users for dropdown
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/users'); // Update with your endpoint
        setUsers(response.data); // Assuming the response returns an array of users
      } catch (err) {
        setError('Error fetching users: ' + err.message);
      }
    };

    fetchUsers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (role === 'Select Role') {
      setError('Please select a role.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/create-user', {
        firstName,
        lastName,
        email,
        username,
        password,
        role,
      });

      setSuccess('User created successfully!');
      setError('');
      // Clear the fields
      setFirstName('');
      setLastName('');
      setEmail('');
      setUsername('');
      setPassword('');
      setRole('Select Role');
    } catch (err) {
      setError('Error creating user: ' + err.response?.data?.message || err.message);
      setSuccess('');
    }
  };

  return (
    <div className="create-user">
      <h2>Create User</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="First Name"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Last Name"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <select value={role} onChange={(e) => setRole(e.target.value)} required>
          <option value="Select Role" disabled>Select Role</option>
          <option value="admin">Admin</option>
          <option value="developer">Developer</option>
          <option value="read_user">Read-Only</option>
        </select>
        <button type="submit">Create User</button>
      </form>
      {error && <p className="error">{error}</p>}
      {success && <p className="success">{success}</p>}
    </div>
  );
};

export default CreateUser;
