import React, { useState } from 'react';

const authInfo = {
  token: 'qiwPrSCLxVf1twsT05kfUsA3O5HdgoElsVyTvwZJIDk=',
  server: 'http://localhost:3001/proxy',
};

const FormDisplay = () => {
  const [status, setStatus] = useState('Idle');

  const createContainer = async () => {
    setStatus('Creating container...');
    try {
      const response = await fetch(`${authInfo.server}/rest/container`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${authInfo.token}`, // your proxy converts this to ph-auth-token
        },
        body: JSON.stringify({
          name: 'Test Container from UI',
          label: 'event_data',
          description: 'This container was created from the UI via proxy.',
        }),
      });

      const text = await response.text();

      if (!response.ok) {
        console.error('❌ Raw error response:', text);
        throw new Error(`Failed to create container: ${response.status} ${response.statusText}`);
      }

      const data = JSON.parse(text);
      console.log('✅ Container created:', data);
      setStatus(`Container created (ID: ${data.id})`);
    } catch (err) {
      console.error('❌ createContainer failed:', err);
      setStatus(`Container creation failed: ${err.message}`);
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'sans-serif' }}>
      <h2>Test Container Creation</h2>
      <button onClick={createContainer} style={{ padding: '10px 20px', fontSize: '16px' }}>
        Create Container
      </button>
      <p>Status: {status}</p>
    </div>
  );
};

export default FormDisplay;
