import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { SplunkThemeProvider } from '@splunk/themes';
import Navbar from './components/Navbar';
import FormBuilder from './components/FormBuilder';
import ViewForms from './components/ViewForms';
import Configuration from './components/Configuration';
import FormDisplay from './components/FormDisplay';
import Login from './components/login';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (token) {
      setIsAuthenticated(true);
      setUserRole(role);
    }
  }, []);

  const handleLogin = (role) => {
    setIsAuthenticated(true);
    setUserRole(role);
    localStorage.setItem('role', role);
    console.log('User logged in');
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token to log out
    localStorage.removeItem('role'); // Remove role
    setIsAuthenticated(false);
    setUserRole(null);
    console.log('User logged out');
  };

  const PrivateRoute = ({ element, allowedRoles }) => {
    return isAuthenticated && allowedRoles.includes(userRole) ? (
      element
    ) : (
      <Navigate to="/login" />
    );
  };

  return (
    <SplunkThemeProvider family="enterprise" density="comfortable">
      <Router>
        {isAuthenticated && <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />}
        <div className="content">
          <Routes>
            {/* Authentication Route */}
            <Route path="/login" element={<Login onLogin={handleLogin} />} />

            {/* Protected Routes with Role-Based Access Control */}
            <Route
              path="/"
              element={
                <PrivateRoute
                  element={<ViewForms />}
                  allowedRoles={['admin', 'developer', 'read-only']}
                />
              }
            />
            <Route
              path="/view-forms"
              element={
                <PrivateRoute
                  element={<ViewForms />}
                  allowedRoles={['admin', 'developer', 'read-only']}
                />
              }
            />
            <Route
              path="/configuration"
              element={
                <PrivateRoute element={<Configuration />} allowedRoles={['admin']} />
              }
            />
            <Route
              path="/forms/:formName"
              element={
                <PrivateRoute
                  element={<FormDisplay />}
                  allowedRoles={['admin', 'developer']}
                />
              }
            />
          </Routes>
        </div>
      </Router>
    </SplunkThemeProvider>
  );
};

export default App;
