import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { SplunkThemeProvider } from '@splunk/themes';
import Navbar from './components/Navbar';
import FormBuilder from './components/FormBuilder';
import ViewForms from './components/ViewForms';
import Configuration from './components/Configuration';
import FormDisplay from './components/FormDisplay';
import Login from './components/Login';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token') // Check if token exists in localStorage
  );

  const handleLogin = () => {
    setIsAuthenticated(true);
    console.log('User logged in');
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token to log out
    setIsAuthenticated(false);
    console.log('User logged out');
  };

  return (
    <SplunkThemeProvider family="enterprise" density="comfortable">
      <Router>
        {isAuthenticated && <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />}
        <div className="content">
          <Routes>
            {/* If not authenticated, show Login page as default */}
            {!isAuthenticated && (
              <Route path="/login" element={<Login onLogin={handleLogin} />} />
            )}
            {/* Redirect to login page if trying to access any other route while not authenticated */}
            {!isAuthenticated && (
              <Route path="*" element={<Navigate to="/login" />} />
            )}

            {/* Protected Routes */}
            {isAuthenticated && (
              <>
                {/* Default landing page when authenticated is View Forms */}
                <Route path="/" element={<Navigate to="/view-forms" />} />
                <Route path="/view-forms" element={<ViewForms />} />
                <Route path="/configuration" element={<Configuration />} />
                <Route path="/forms/:formName" element={<FormDisplay />} />
                <Route path="/form-builder" element={<FormBuilder />} />
              </>
            )}
          </Routes>
        </div>
      </Router>
    </SplunkThemeProvider>
  );
};

export default App;
