import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { SplunkThemeProvider } from '@splunk/themes';
import Navbar from './components/Navbar';
import FormBuilder from './components/FormBuilder';
import ViewForms from './components/ViewForms';
import Configuration from './components/Configuration';
import FormDisplay from './components/FormDisplay';
import Login from './components/Login';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token') // Check if token exists in localStorage
  );
  const [userRole, setUserRole] = useState(''); // State to hold user role
  const [username, setUsername] = useState(''); // State to hold username

  const handleLogin = (role, user) => {
    setIsAuthenticated(true);
    setUserRole(role); // Set user role on login
    setUsername(user); // Set username on login
    console.log('User logged in with role:', role);
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token to log out
    setIsAuthenticated(false);
    setUserRole(''); // Reset user role on logout
    setUsername(''); // Reset username on logout
    console.log('User logged out');
  };

  // Function to check if user has access to a route
  const hasAccess = (roles) => {
    return roles.includes(userRole);
  };

  return (
    <SplunkThemeProvider family="enterprise" density="comfortable">
      <Router>
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} username={username} />
        <div className="content">
          <Routes>
            {/* Redirect from root to view-forms if authenticated */}
            <Route path="/" element={<Navigate to={isAuthenticated ? "/view-forms" : "/login"} />} />
            
            {/* Authentication Route */}
            <Route path="/login" element={isAuthenticated ? <Navigate to="/view-forms" /> : <Login onLogin={handleLogin} />} />

            {/* Protected Routes */}
            <Route
              path="/view-forms"
              element={isAuthenticated ? <ViewForms /> : <Navigate to="/login" />}
            />
            <Route
              path="/form-builder"
              element={isAuthenticated && hasAccess(['admin_user', 'dev_user']) ? <FormBuilder /> : <Navigate to="/login" />}
            />
            <Route
              path="/configuration"
              element={isAuthenticated && hasAccess(['admin_user', 'dev_user']) ? <Configuration /> : <Navigate to="/login" />}
            />
            <Route
              path="/forms/:formName"
              element={isAuthenticated ? <FormDisplay /> : <Navigate to="/login" />}
            />
            {/* Catch all route for unauthorized access */}
            <Route path="*" element={<Navigate to={isAuthenticated ? "/view-forms" : "/login"} />} />
          </Routes>
        </div>
      </Router>
    </SplunkThemeProvider>
  );
};

export default App;
