import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { SplunkThemeProvider } from '@splunk/themes';
import Navbar from './components/Navbar';
import FormBuilder from './components/FormBuilder';
import ViewForms from './components/ViewForms';
import Configuration from './components/Configuration';
import FormDisplay from './components/FormDisplay';
import Login from './components/Login';
import AdminPage from './components/AdminPage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem('token')
  );
  const [userRole, setUserRole] = useState('');
  const [username, setUsername] = useState('');

  const handleLogin = (role, user) => {
    setIsAuthenticated(true);
    setUserRole(role);
    setUsername(user);
    console.log('User logged in');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
    setUserRole('');
    setUsername('');
    console.log('User logged out');
  };

  return (
    <SplunkThemeProvider family="enterprise" density="comfortable">
      <Router>
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} username={username} />
        <div className="content">
          <Routes>
            <Route path="/" element={<Navigate to={isAuthenticated ? "/view-forms" : "/login"} />} />
            <Route path="/login" element={isAuthenticated ? <Navigate to="/view-forms" /> : <Login onLogin={handleLogin} />} />
            <Route path="/view-forms" element={isAuthenticated ? <ViewForms /> : <Navigate to="/login" />} />
            <Route path="/form-builder" element={isAuthenticated && (userRole === 'admin' || userRole === 'developer') ? <FormBuilder /> : <Navigate to="/login" />} />
            <Route path="/configuration" element={isAuthenticated && (userRole === 'admin' || userRole === 'developer') ? <Configuration /> : <Navigate to="/login" />} />
            <Route path="/admin" element={isAuthenticated && userRole === 'admin' ? <AdminPage /> : <Navigate to="/login" />} />
            <Route path="/forms/:formName" element={isAuthenticated ? <FormDisplay /> : <Navigate to="/login" />} />
          </Routes>
        </div>
      </Router>
    </SplunkThemeProvider>
  );
};

export default App;
